一个简单的HP模拟，询问当前的操作，并输出当前玩家的状态 \
#include &lt;iostream&gt; #include &lt;string&gt; using namespace std; int main() { int hp = 50; char ch = &#39;n&#39;; int change = 0; cout &lt;&lt; &#34;初始HP为&#34; &lt;&lt; to_string(hp) &lt;&lt; endl; cout &lt;&lt; &#34;请选择 伤害 或 治疗，用字母a或b表示&#34; &lt;&lt; endl; cin &gt;&gt; ch; cout &lt;&lt; &#34;请输入伤害或治疗的数值&#34; &lt;&lt; endl; cin &gt;&gt; change; if (ch == &#39;a&#39; || ch == &#39;b&#39;) { if (ch == &#39;a&#39;) { cout &lt;&lt; &#34;您选择了伤害&#34; &lt;&lt; to_string(change) &lt;&lt; endl; hp -= change; } else { cout &lt;&lt; &#34;您选择了治疗&#34; &lt;&lt; to_string(change) &lt;&lt; endl; hp += change; } if (hp &gt; 100) { hp = 100; } if (hp &lt; 0) { hp = 0; } cout &lt;&lt; &#34;玩家当前HP为&#34; &lt;&lt; to_string(hp) &lt;&lt; endl &lt;&lt; &#34;当前玩家状态为&#34;; if (hp &lt;= 0) { cout &lt;&lt; &#34;死亡&#34;; } else if (hp &lt; 20) { cout &lt;&lt; &#34;濒死&#34;; } else if (hp &lt; 80) { cout &lt;&lt; &#34;正常&#34;; } else { cout &lt;&lt; &#34;完美&#34;; } } else { cout &lt;&lt; &#34;请重新输入&#34;; } return 0; }
